#include <sys/stat.h>
#include <unistd.h>
#include <libgen.h>

#define PATH_MAX 512

/* Extrair el contingut del fitxer tar a la ruta de destinació donada. Si l'extracció és exitosa, torna veritable; si no, si es produeix qualsevol error durant el procés d'extracció (per exemple, fitxer no es troba, error de permís, no es pot extrair tot el contingut), només torna fals. suggeriment: El següent codi mostra com extrair el contingut del fitxer tar a la ruta de destinació donada amb la biblioteca libarchive. bool extract_archive(struct archive *src, const char *dest_path) {struct archive *out; struct archive_entry *entry *entry; intpare flags = ARCHIVE_src_EXTRACT_TIME ARCHIVE_TRACT_PERM; int; r; char_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_r_ */// Iniciar l'escriptor d'arxiu    out = archive_write_disk_new();
    if (!out) {
        return false;
    }
    archive_write_disk_set_options(out, flags);
// Llegeix i extrageix cada entrada    while ((r = archive_read_next_header(src, &entry)) == ARCHIVE_OK) {
        // Crear camí complet        snprintf(full_path, sizeof(full_path), "%s/%s",
                dest_path, archive_entry_pathname(entry));
        archive_entry_set_pathname(entry, full_path);
// Escriure encapçallador        r = archive_write_header(out, entry);
        if (r != ARCHIVE_OK) {
            archive_write_free(out);
            return false;
        }
// Copiar dades        const void *buff;
        size_t size;
        la_int64_t offset;
        while ((r = archive_read_data_block(src, &buff, &size, &offset)) == ARCHIVE_OK) {
            if (archive_write_data_block(out, buff, size, offset) != ARCHIVE_OK) {
                archive_write_free(out);
                return false;
            }
        }
        if (r != ARCHIVE_EOF) {
            archive_write_free(out);
            return false;
        }
    }

    archive_write_free(out);
    return r == ARCHIVE_EOF ? true : false;
}


