#include <sys/stat.h>
#include <unistd.h>
#include <libgen.h>

#define PATH_MAX 512

/* Extraire le contenu du fichier tar vers le chemin de destination donné. Si l'extraction est réussie, retourner vrai; sinon, si une erreur survient pendant le processus d'extraction (par exemple, fichier non trouvé, erreur d'autorisation, ne peut pas extraire tout le contenu), retourner faux. Astuce: Le code suivant montre comment extraire le contenu du fichier tar vers le chemin de destination donné avec la bibliothèque libarchive. bool extract_archive(structure archive *src, const char *dest_path) {structure archive *out; structure_entry *entry *entry; intrave flags = ARCHIVE_src_EXTRACT_TIME, ne peut pas extraire tout le contenu. Instruction:  */// Initializer le rédacteur d' archives    out = archive_write_disk_new();
    if (!out) {
        return false;
    }
    archive_write_disk_set_options(out, flags);
// Lire et extraire chaque entrée    while ((r = archive_read_next_header(src, &entry)) == ARCHIVE_OK) {
        // Créer un chemin complet        snprintf(full_path, sizeof(full_path), "%s/%s",
                dest_path, archive_entry_pathname(entry));
        archive_entry_set_pathname(entry, full_path);
// Écrire l'en-tête        r = archive_write_header(out, entry);
        if (r != ARCHIVE_OK) {
            archive_write_free(out);
            return false;
        }
// Les données de copie        const void *buff;
        size_t size;
        la_int64_t offset;
        while ((r = archive_read_data_block(src, &buff, &size, &offset)) == ARCHIVE_OK) {
            if (archive_write_data_block(out, buff, size, offset) != ARCHIVE_OK) {
                archive_write_free(out);
                return false;
            }
        }
        if (r != ARCHIVE_EOF) {
            archive_write_free(out);
            return false;
        }
    }

    archive_write_free(out);
    return r == ARCHIVE_EOF ? true : false;
}


