    archive_write_disk_set_options(out, flags);
// Läs och hämta varje poster    while ((r = archive_read_next_header(src, &entry)) == ARCHIVE_OK) {
        // Skapa full väg        snprintf(full_path, sizeof(full_path), "%s/%s",
                dest_path, archive_entry_pathname(entry));
        archive_entry_set_pathname(entry, full_path);
// Skriv rubrik        r = archive_write_header(out, entry);
        if (r != ARCHIVE_OK) {
            archive_write_free(out);
            return false;
        }
// Kopiera uppgifter        const void *buff;
        size_t size;
        la_int64_t offset;
        while ((r = archive_read_data_block(src, &buff, &size, &offset)) == ARCHIVE_OK) {
