// Ghi tiêu đề        r = archive_write_header(out, entry);
        if (r != ARCHIVE_OK) {
            archive_write_free(out);
            return false;
        }
// Sao chép dữ liệu        const void *buff;
        size_t size;
        la_int64_t offset;
